graphql.query:
query ($characterId: Int!, $locationId: Int!, $episodeId: Int!) {
  character(characterId: $characterId) {
    name
    gender
    status
    id
  }
  location(locationId: $locationId) {
    name
    dimension
  }
  episode(episodeId: $episodeId) {
    name
    air_date
    episode
  }
  characters(filters: {name: "Rahul"}) {
    info {
      count
    }
    result {
      name
      type
    }
  }
  episodes(filters: {episode: "hulu"}) {
    result {
      id
      name
      air_date
      episode
    }
  }
}

mutation ($locationName: String!, $characterName: String!, $episodeName: String!) {
  createLocation(location: {name: $locationName, type: "Southzone", dimension: "234"}) {
    id
  }
  createCharacter(character: {name: $characterName, type: "Macho", status: "dead", species: "fantasy", gender: "male", image: "png", originId: 15, locationId: 15}) {
    id
  }
  createEpisode(episode: {name: $episodeName, air_date: "1990 June", episode: "1"}) {
    id
  }
  deleteLocations(locationIds: [17, 14]) {
    locationsDeleted
  }
}
